css:
@font-face {
  font-family: LillyMae;
  src: url('files/LillyMae-Regular.woff');
}
@font-face {
  font-family: Kepler;
  src: url('files/Kepler-Std-Display.woff');
}
body, p, div, table, tr, td, th, span, small{
  font-family: Kepler;
}
.row > .col-xs-6:first-child{
    padding-right: 5px ;
  }
  .row > .col-xs-6:last-child{
    padding-left: 5px ;
  }
  .addressbox{
    border: 2px solid #d4c18e;
    padding: 10px;
  }
  .addressbox p{
    margin-bottom: 0;
  }
  .addressbox hr{
    margin: 2px -10px;
      padding: 0;
      border-color: #000;
  }
  .table th{
    border:1px solid #000;
  }
  .logodiv {
    text-align: center;
    
  }
  .logosec{
    padding-top: 0.5rem;
    padding-bottom: 1.4rem;
  }
  .lilly{
     font-family: LillyMae !important;
  }
  .width-50{
    width: 50px;
  }
  p.title{
    font-size:22px;
    margin-top: -30px;
    margin-bottom: 0;
    color: #000;
  }
  .tablesp>tbody>tr>th{
        border-top: 1px solid #000;
        border-bottom: 1px solid #000 !important;
        background: #f5f1e6 !important;
  }
  .tablesp > tbody > tr > .lilly{
    font-size: 24px;
  }
  .footertag{margin: 10px 0;}
  .footertag, .footertag p{
    text-align: center;
    color: #ceb985;
  }
  .footertag p.lilly{
    font-size: 36px;
  }
   .footertag p{
    font-size: 22px;
   }
  .float-left{
    float: left;
    margin-right: 5px;
  }
  .footer-html .float-left{
    max-width: 90px;
     width: 90px;
  }
  .max25{
    max-width: 13px !important;
        margin-right: 3px;
        width: 13px !important;
  }
  .footericon{
    text-align: center;
    margin-bottom: 10px;
  }
  .footericon img{
    width: 20px;
    display: inline-block;
    max-width: 20px;
  }
  .footer-html .colf, .footer-html .row{
    padding: 0 !important;
    margin:0  !important;
  }
  .footer-html .colf{
    padding: 0 2px;    
  }
  .tablesp>tbody>tr>td {
    border-right: 1px solid #d1d8dd;
  }
  .tablesp>tbody>tr>td:last-child {
    border-right: 0px solid #d1d8dd;
  }
  .print-format .tablelow th{
    background:none !important;   
    font-weight: normal;
        vertical-align: top !important;
    padding: 1px 2px !important;
  }
   .tablelow{
       width:100%;
       max-width:100%;
   }
     .print-format .tablelow td{
      padding: 2px 2px !important;
      border-top: 0 !important;
     }
