css:
@font-face {
  font-family: LillyMae;
  src: url('/files/LillyMae-Regular.woff');
}
@font-face {
  font-family: Kepler;
  src: url('/files/Kepler-Std-Display.woff');
}
body, p, div, table, tr, td, th, span, small{
  font-family: Kepler;
}
.row > .col-xs-6:first-child{
    padding-right: 5px ;
  }
  .row > .col-xs-6:last-child{
    padding-left: 5px ;
  }
  .addressbox{
    border: 2px solid #d4c18e;
    padding: 10px;
  }
  .addressbox p{
    margin-bottom: 0;
  }
  .addressbox hr{
    margin: 2px -10px;
      padding: 0;
      border-color: #000;
  }
  .table th{
    border:1px solid #000;
  }
  .logodiv {
    text-align: center;
  }
  .logosec{
    padding-top: 1rem;
    padding-bottom: 1.4rem;
  }
  .lilly{
     font-family: LillyMae !important;
  }
  .width-50{
    width: 50px;
  }
  p.title{
    font-size:22px;
    margin-top: -30px;
    margin-bottom: 0;
    color: #000;
  }
  .table>tbody>tr>th{
        border-top: 1px solid #000;
        border-bottom: 1px solid #000 !important;
        background: #f5f1e6 !important;
  }
  .table>thead>tr>th{
        border-top: 1px solid #000;
        border-bottom: 1px solid #000 !important;
        background: #f5f1e6 !important;
  }    
  .table > tbody > tr > .lilly{
    font-size: 24px;
  }
  .footertag{margin: 10px 0;}
  .footertag, .footertag p{
    text-align: center;
    color: #ceb985;
  }
  .footertag p.lilly{
    font-size: 36px;
  }
   .footertag p{
    font-size: 22px;
   }
  .float-left{
    float: left;
    margin-right: 5px;
  }
  .footer-html .float-left{
    max-width: 90px;
     width: 90px;
  }
  .max25{
    max-width: 13px !important;
        margin-right: 3px;
        width: 13px !important;
  }
  .footericon{
    text-align: center;
    margin-bottom: 10px;
  }
  .footericon img{
    width: 20px;
    display: inline-block;
    max-width: 20px;
  }
  .footer-html .colf, .footer-html .row{
    padding: 0 !important;
    margin:0  !important;
  }
  .footer-html .colf{
    padding: 0 2px;    
  }
  .table>tbody>tr>td {
    border-right: 1px solid #d1d8dd;
  }
   .table>tbody>tr>td:last-child {
    border-right: 0px solid #d1d8dd;
   }
  .table-total{
    border: 2px solid #d4c18e;
    width: 100%;
  }
  .table-total tr, .table-total tr td {
    border: none !important;
 }
 .table-total tr td {
    padding: 5px;
    text-align: right;
    font-size: 14px;
 }
.table-total tr th {
  text-transform: uppercase;
  text-align: left;
  font-size: 12px;
  background: #fff !important;
}
 .table-total tr:last-child td, .table-total tr:last-child th {
    background: #f5f1e6 !important;
 }
